DROP TABLE IF EXISTS `order`;
CREATE TABLE `order` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `order_sn` varchar(30) COLLATE utf8_unicode_ci NOT NULL COMMENT '订单id',
  `ads_user_id` int(11) NOT NULL COMMENT '广告主id',
  `order_type` tinyint(4) NOT NULL DEFAULT '1' COMMENT '订单状态1预约状态(等待接受)2拒绝3流单4正执行5完成6申诉',
  `title` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '活动标题',
  `type_id` int(11) NOT NULL COMMENT '活动类型id',
  `type_name` varchar(200) COLLATE utf8_unicode_ci DEFAULT '0' COMMENT '活动类型(订单类型)',
  `user_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '消费总金额',
  `start_at` timestamp NULL DEFAULT NULL,
  `over_at` timestamp NULL DEFAULT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `doc_type` tinyint(4) DEFAULT NULL COMMENT '1外部链接2上传文档3内部编辑',
  `content` text COLLATE utf8_unicode_ci COMMENT '文档内容',
  `keywords` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '关键词',
  `remark` text COLLATE utf8_unicode_ci COMMENT '备注',
  `cooperation_mode` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '合作方式(目前针对视频营销)',
  `cooperation_place` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '合作地点(目前针对视频营销)',
  `sale_file` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL COMMENT '上传附件(目前针对视频营销)',
  PRIMARY KEY (`id`),
  UNIQUE KEY `order_order_id_unique` (`order_sn`),
  KEY `order_ads_user_id_index` (`ads_user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=54 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

